import React from "react";
import "date-fns";
import {
  Paper,
  Box,
  Typography,
  Divider,
  Container,
  InputLabel,
  Select,
  FormControl,
  FormHelperText,
  MenuItem,
  Grid,
  Button,
} from "@material-ui/core";
import { connect } from "react-redux";
import { ThemeProvider } from "@material-ui/core/styles";
import { theme } from "../../theme/light";
import { withStyles } from "@material-ui/core/styles";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from "@material-ui/pickers";
import UserService from "../../services/user.service";

const useStyles = (theme) => ({
  root: {
    "& > *": {
      marginTop: theme.spacing(4),
    },
  },
  paper: {
    width: "100%",
  },

  formControl: {
    marginTop: theme.spacing(2),
    minWidth: 300,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
});
class Home extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      age: "",
      startDate: new Date(),
      endDate: new Date() + 1,
      deviceuniqueid: "Web",
      token: "339CFB64EDC6E94C5577CD68319E72AB6732C711AE661427AC83931B33F05CC4",
      mappingList: [],
      consolidates: "",
      type: "",
    };
  }

  componentDidMount() {
    const { userDetails } = this.props;
    const { loginToken } = userDetails;
    this.setState(
      {
        token: loginToken,
      },
      () => {
        this.getRefreshMapping();
      }
    );
  }

  getRefreshMapping = async () => {
    const { deviceuniqueid, token } = this.state;

    try {
      const response = await UserService.mapping(deviceuniqueid, token);
      const { status, data } = response;
      const { groupmapping } = data;
      const { data: list } = groupmapping;
      console.log("response after getRefreshMapping --> ", response);
      if (data && list.length > 0) {
        let id = [];
        list.map((item, index) => {
          id.push(item.instanceid);
        });
        id = id.join(",");
        this.setState({
          mappingList: list,
          consolidates: id,
        });
      }
    } catch (error) {
      console.log("status getRefreshMapping", error);
    }
  };

  handleChange = (event) => {
    this.setState({
      age: event.target.value,
    });
  };

  handleTypeChange = (event) => {
    this.setState({
      type: event.target.value,
    });
  };

  handleDateChange = (date) => {
    this.setState({
      startDate: date,
      endDate: date + 1,
    });
  };
  handleEndDateChange = (date) => {
    this.setState({
      endDate: date,
    });
  };
  render() {
    const { classes } = this.props;
    const { age, startDate, endDate, mappingList, type } = this.state;
    console.log("mappingList", mappingList, " type", type);
    return (
      <ThemeProvider theme={theme}>
        <Box className="container" p={2.5} bgcolor="primary.lightBgContainer">
          <Paper className="content lead-page">
            <Box className="page-heading" mb={3}>
              <Box color="text.secondary" pt={3} pr={2.7} pb={1.8} pl={3}>
                <Typography variant="h6" gutterBottom color="inherit">
                  Dashboard
                </Typography>
              </Box>
              <Divider />
            </Box>
            <MuiPickersUtilsProvider utils={DateFnsUtils}>
              <Grid container justifyContent="space-evenly">
                <FormControl variant="standard" className={classes.formControl}>
                  <InputLabel htmlFor="age-native-simple">
                    Select Faciliy
                  </InputLabel>
                  <Select
                    native
                    value={age}
                    placeholder={"Age"}
                    onChange={(e) => this.handleChange(e)}
                  >
                    <option value="0">Consolidated</option>

                    {mappingList.map((data, index) => {
                      return (
                        <option key={index.toString()} value={data.instanceid}>
                          {data.name}
                        </option>
                      );
                    })}
                  </Select>
                </FormControl>
                <FormControl variant="standard" className={classes.formControl}>
                  <InputLabel htmlFor="age-native-simple">
                    Select Type
                  </InputLabel>
                  <Select
                    native
                    value={type}
                    placeholder={"Age"}
                    onChange={(e) => this.handleTypeChange(e)}
                  >
                    <option aria-label="None" value="NA" />
                    <option value={"Daily"}>Daily</option>
                    <option value={"Monthly"}>Monthly</option>
                  </Select>
                </FormControl>

                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="dd/MM/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Date"
                  value={startDate}
                  onChange={(date) => this.handleDateChange(date)}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
                {/* <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="dd/MM/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="End Date"
                  minDate={startDate}
                  value={endDate}
                  onChange={(date) => this.handleEndDateChange(date)}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                /> */}
                <div className={classes.root}>
                  <Button variant="contained" size="small" color="primary">
                    Submit
                  </Button>
                </div>
              </Grid>
            </MuiPickersUtilsProvider>
          </Paper>
        </Box>
      </ThemeProvider>
    );
  }
}

const mapStateToProps = (state) => ({
  userDetails: state.user,
});

export default connect(mapStateToProps)(withStyles(useStyles)(Home));
